import React, {useState} from 'react'
import '../css/SecondaryHeader.css'
import { useTaskContext } from '../TaskContext';

const SecondaryHeader = () => {
    const { addTask } = useTaskContext();
    const [taskTitle, setTaskTitle] = useState('');
    const [taskDescription, setTaskDescription] = useState('');

    const handleAddTask = () => {
        if (!taskTitle.trim()) return; // Prevent adding tasks without a title

        const newTask = {
            id: Date.now(),
            title: taskTitle,
            description: taskDescription,
            completed: false
        };
        addTask(newTask);
        setTaskTitle('');
        setTaskDescription('');
    };

    return (
        <div class="secondary-header">
              <button style={{opacity: 0}} class="toggle-sidebar" onclick="openNav()">&#9776; Open Sidebar</button>
            <button class="add-task">Add Task
                <img src="add.png" alt="Add Icon" className='add-icon'></img>
            </button>
        </div>
    );

}

export default SecondaryHeader;
